      state.value -= 1;
    },
    // Use the PayloadAction type to declare the contents of `action.payload`
    calculationByAmount: (state, action) => {
      let info=action.payload;
      let data=[];
      if(state.doing==0){
        if(state.clicknumber==0)
        {
          console.log('clickonce')
          state.cal_display+=action.payload['number']
          state.clicknumber++
        }   
        else{
           console.log('clickmany')
            state.cal_display+=action.payload['number'].toString()
        }    
      }
      else
      {
        state.cal_display+=action.payload['number']
      }

      //state.cal_display+=action.payload['number']
      state.value=0
      let lastNumber=(state.cal_display.toString()).split(/[\s()*/%+-]+/)
      //let lastNumber=(state.cal_display.toString()).match((/-?\d/g))
      // console.log(state.cal_display)
      // if(lastNumber.length==1){
      //   if(state.cal_display==0)
      //   {
      //     state.cal_display+=action.payload['number']
      //   }
      //   else{
      //     state.cal_display+=action.payload['number'].toString()
      //   }
      // }
      // else{

      // }
      if(info['operation']=='+')
      {
        console.log('sumdata')
        state.doing=1
        if(lastNumber.length>3)
        {
          state.value=parseInt(state.result);
          state.value += parseInt(lastNumber[lastNumber.length-1]);
        }
        else{
          for(let i=0 ; i<lastNumber.length;i++)
          {
            state.value += parseInt(lastNumber[i]);
          }
        }
      }
      else if(info['operation']=='-'){
        state.doing=1
        state.value=state.result;
        state.value -= lastNumber[lastNumber.length-1];
       }
       console.log(state.result)

    },

  },
  // The `extraReducers` field lets the slice handle actions defined elsewhere,
  // including actions generated by createAsyncThunk or in other slices.
  extraReducers: (builder) => {
    builder
      .addCase(incrementAsync.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(incrementAsync.fulfilled, (state, action) => {
        state.status = 'idle';
        state.value += action.payload;
      });
  },
});

export const { increment, decrement, calculationByAmount,decrementByAmount,operation,reset } = counterSlice.actions;

